class SowestartApp{constructor(){this.initialized=false}async init(){if(this.initialized)return;try{await this.initializeComponents();this.setupGreeting();this.listenForThemeChanges();this.initialized=true;console.log("Sowestart initialized successfully")}catch(error){console.error("Failed to initialize Sowestart:",error)}}async initializeComponents(){i18n.init();backgroundManager.init().catch(err=>{console.warn("Background initialization failed:",err)});clockManager.init();weatherManager.init().catch(err=>{console.warn("Weather initialization failed:",err)});searchManager.init();linksManager.init();settingsManager.init()}setupGreeting(){const greetingText=document.getElementById("greeting-text");const greetingName=document.getElementById("greeting-name");const timeOfDay=utils.getTimeOfDay();const language=storage.get("language");const userName=storage.get("userName");greetingText.textContent=utils.getGreeting(timeOfDay,language);greetingName.textContent=userName;setInterval(()=>{const newTimeOfDay=utils.getTimeOfDay();if(newTimeOfDay!==timeOfDay){greetingText.textContent=utils.getGreeting(newTimeOfDay,language)}},6e4);greetingName.addEventListener("blur",e=>{const newName=e.target.textContent.trim();if(newName){storage.set("userName",newName)}else{e.target.textContent=storage.get("userName")}});greetingName.addEventListener("keydown",e=>{if(e.key==="Enter"){e.preventDefault();greetingName.blur()}})}listenForThemeChanges(){const theme=storage.get("theme");if(theme!=="auto")return;const mediaQuery=window.matchMedia("(prefers-color-scheme: dark)");mediaQuery.addEventListener("change",e=>{if(storage.get("theme")==="auto"){settingsManager.applyTheme("auto")}})}destroy(){clockManager.destroy();weatherManager.destroy();this.initialized=false}}let app;if(document.readyState==="loading"){document.addEventListener("DOMContentLoaded",()=>{app=new SowestartApp;app.init()})}else{app=new SowestartApp;app.init()}window.addEventListener("beforeunload",()=>{if(app){app.destroy()}});